YafBBCode.DisplayJS:
function toggleSpoiler(btn, elid)
{
var el = document.getElementById(elid);
if (el == null)
return;
if (el.style.display == '')
{
el.style.display = 'none';
btn.value='Show Spoiler';
}
else
{
el.style.display = '';
btn.value='Hide Spoiler';
}
}

YafBBCode.DisplayCSS:
.spoilerbox
{
margin: 5px;
padding: 4px;
background-color: #eeeeee;
border: solid 1px #808080;
color: #000000;
}
